import './App.css';
import { LoginPage } from './pages/home/login';
import { CadastroPage } from './pages/home/cadastro';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import { createContext, useState } from 'react';

const AuthContext = createContext({} as any);
export type signInDataType = {
  username?: string;
  password?: string;
  hostname?: string;
}
function App() {
  const [user, setUser] = useState({} as any);


  function signIn(data: signInDataType) {

    function doAjaxSignIn(username: string, password: string, hostname: string, successCallBack: (data: any) => void, errorCallBack: (data: any) => void) {
      Z.ajax({
        method: 'POST',
        url: 'http://localhost:8181/Login/index/sign/',
        with_credentials: true,
        headers: { "Authorization": "Credentials " + hostname + ":" + username + "@" + password, },
        data: { remember_me: true },
        success: function (data) {
          console.log('Login response: ', data);
          successCallBack(data);
        },
        error: function (response) {
          var data = (response?.responseJSON || response);
          console.log('Login error response: ', data);
          errorCallBack(data);
        }
      });
    }

    const successAjaxSignInCallback = function (data: any) {
      console.log('successAjaxSignInCallback Handle: ', data);
      setUser(data);
      return;
    }

    const errorAjaxSignInCallback = function (data: any) {
      console.log('errorAjaxSignInCallback Handle: ', data);
      return;
    }

    doAjaxSignIn((data?.username || ''), (data?.password || ''), (data?.hostname || ''), successAjaxSignInCallback, errorAjaxSignInCallback);
    return;
  }

  return (
    <BrowserRouter>
      <AuthContext.Provider value={{ user, signIn }}>
        <Switch>
          <Route path="/" exact component={LoginPage} />
          <Route path="/login" exact component={LoginPage} />
          <Route path="/cadastro" exact component={CadastroPage} />
        </Switch>
      </AuthContext.Provider>

    </BrowserRouter>
  );
}

export default App;
